import React, { useState, useEffect } from "react";
import "./styles.css";

function App() {
  const [mousePosition, setMousePosition] = useState({ x: 50, y: 50 });
  const [isLoaded, setIsLoaded] = useState(false);
  const [hoveredLink, setHoveredLink] = useState(null);

  useEffect(() => {
    const handleMouseMove = (e) => {
      const x = (e.clientX / window.innerWidth) * 100;
      const y = (e.clientY / window.innerHeight) * 100;
      setMousePosition({ x, y });
    };

    window.addEventListener("mousemove", handleMouseMove);

    // Trigger load animation
    setTimeout(() => setIsLoaded(true), 100);

    return () => window.removeEventListener("mousemove", handleMouseMove);
  }, []);

  const links = [
    { name: "Email", href: "mailto:pitkanenville@proton.me", label: "Send an email" },
    { name: "LinkedIn", href: "https://www.linkedin.com/in/ville-pitk%C3%A4nen-10456719b/", label: "Connect on LinkedIn" },
    { name: "GitHub", href: "https://github.com/villepp", label: "View projects" }
  ];

  return (
    <div className="app">
      {/* Animated gradient background */}
      <div 
        className="gradient-bg"
        style={{
          background: `radial-gradient(circle at ${mousePosition.x}% ${mousePosition.y}%, 
            rgba(99, 102, 241, 0.15) 0%, 
            rgba(99, 102, 241, 0.05) 25%, 
            transparent 50%)`
        }}
      />

      {/* Decorative elements */}
      <div className="floating-shapes">
        <div className="shape shape-1"></div>
        <div className="shape shape-2"></div>
        <div className="shape shape-3"></div>
      </div>

      <main className={`content ${isLoaded ? 'loaded' : ''}`}>
        <div className="intro">
          <h1 className="name">
            <span className="name-part">Ville</span>
            <span className="name-part accent">Pitk√§nen</span>
          </h1>

          <div className="title-wrapper">
            <p className="title">
              Information Technology Student
              <span className="divider">√ó</span>
              Developer
            </p>
          </div>
        </div>

        <nav className="links-container">
          {links.map((link, index) => (
            <a
              key={link.name}
              href={link.href}
              className={`link ${hoveredLink === index ? 'hovered' : ''}`}
              target={link.name !== 'Email' ? '_blank' : undefined}
              rel={link.name !== 'Email' ? 'noopener noreferrer' : undefined}
              onMouseEnter={() => setHoveredLink(index)}
              onMouseLeave={() => setHoveredLink(null)}
              style={{ animationDelay: `${0.6 + index * 0.1}s` }}
            >
              <span className="link-text">{link.name}</span>
              <span className="link-hint">{link.label}</span>
              <span className="link-arrow">‚Üí</span>
            </a>
          ))}
        </nav>

        <footer className="footer">
          <div className="status">
            <span className="status-dot"></span>
            Available for opportunities
          </div>
        </footer>
      </main>

      {/* Grid overlay for texture */}
      <div className="grid-overlay"></div>
    </div>
  );
}

export default App;
